mapper.select:
  
    SELECT t.id, t.password, t.status, t.invitecode, t.usedtime, t.createtime FROM t_tgks_mar_passwordcard t


        1=1
    
        AND t.ID = #{id}
    
    
        AND t.PASSWORD = #{password}
    
    
        AND t.STATUS = #{status}
    
    
        AND t.INVITECODE = #{inviteCode}
    
    
        AND t.USEDTIME = #{usedTime}
    
    
        AND t.CREATETIME >= #{createTimeStart}
    
    
        AND #{createTimeEnd} >= t.CREATETIME
    


    ORDER BY ${sortSql}
